const ORGCHART = "https://organization.lascasasgaming.com/images/orgchart_lcgi.jpeg"

class Team extends Component {
  constructor(props) {
    super(props);

    this.handleResize = this.handleResize.bind(this);
    this.closeModal = this.closeModal.bind(this);
    this.state = {
      width: window.innerWidth,
      height: window.innerHeight,
      modalIsOpen: false,
      selectedItem: null,
    };
  }

  componentDidMount() {
    window.addEventListener("resize", this.handleResize);
  }

  componentWillUnmount() {
    window.removeEventListener("resize", this.handleResize);
  }

  handleResize() {
    this.setState({
      width: window.innerWidth,
      height: window.innerHeight,
    });
  }

  openModal = (item) => {
    this.setState({ modalIsOpen: true, selectedItem: item });
  };

  closeModal() {
    this.setState({ modalIsOpen: false, selectedItem: null });
  }

  downloadFileAtURL = (url) => {
    const fileName = url.split("/").pop();
    const aTag = document.createElement("a");
    aTag.href = url;
    aTag.setAttribute("download", fileName);
    document.body.appendChild(aTag);
    aTag.click();
    aTag.remove();
  };


  render() {
    var photos = {
      a: "/images/default_user.jpg",
      //IT
      b1: "/images/alvin.jpg",
      b4: "/images/rafael.jpeg",
      b7: "/images/gino.jpeg",
      b8: "/images/dean.jpg",
      b9: "/images/donel.jpg",
      b10: "/images/johnpaul.jpg",
      //CSR
      c2: "/images/kevin.png",
      c5: "/images/silva.jpeg",
      c7: "/images/grace.jpeg",
      c8: "/images/john_csr.jpg",
      //FINANCE
      d1: "/images/alan.jpeg",
      d2: "/images/bryan.jpg",
      d3: "/images/justin.jpg",
      //HR
      e1: "/images/default_user.jpg",
      e2: "/images/step.jpg",
      e3: "/images/jerome_angelo.jpeg",
      //MARKETING
      f1: "/images/sharmaine.jpeg",
      f2: "/images/jerome.jpeg",
      f5: "/images/jane.jpg",
      f6: "/images/bernard.jpg",
      f7: "/images/jayce.png",
      f8: "/images/kyle.jpg",
      f9: "/images/angelo.png",
      //BOSS
      g1: "/images/joms.jpeg",
      g2: "/images/kenniedy.jpeg",
      g3: "/images/ana.jpeg",
      g4: "/images/alphie.png",
      g5: "/images/leo.jpeg",
      g6: "/images/gian.jpeg",
      g7: "/images/rina.jpeg",
      g8: "/images/dara.jpg",
